import React from "react";
import { Route, Routes } from "react-router-dom";

import Navbar from "./components/navbar";
import RecipeList from "./components/recipeList"; // repurposed as RecipeList
import RecipeDetails from "./components/recipeDetails"; // new component for recipe details
import Edit from "./components/edit";
import Create from "./components/create";

const App = () => {
  return (
    <div>
      <Navbar />
      <Routes>
        {/* The RecipeList component is now the parent for the nested recipe details */}
        <Route path="/" element={<RecipeList />}>
          <Route path="recipe/:id" element={<RecipeDetails />} />
        </Route>
        <Route path="/create" element={<Create />} />
        <Route path="/edit/:id" element={<Edit />} />
      </Routes>
    </div>
  );
};

export default App;
