import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Recipe = (props) => (
  <tr>
    <td>
      <Link to={`recipe/${props.recipe._id}`}>{props.recipe.name}</Link>
    </td>
    <td>{props.recipe.ingredients}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.recipe._id}`}>
        Edit
      </Link>
      |
      <button className="btn btn-link"
        onClick={() => props.deleteRecipe(props.recipe._id)}>
        Delete
      </button>
    </td>
  </tr>
);

export default function RecipeList() {
  const [recipes, setRecipes] = useState([]);

  // Fetch the list of recipes from the backend.
  useEffect(() => {
    async function getRecipes() {
      const response = await fetch("Replace your API URL/recipe");

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const recipes = await response.json();
      setRecipes(recipes);
    }

    getRecipes();
    return;
  }, [recipes.length]);

  // Delete a recipe
  async function deleteRecipe(id) {
    await fetch(`https://0kxdfj8s-5050.usw2.devtunnels.ms/recipe/${id}`, {
      method: "DELETE",
    });

    const newRecipes = recipes.filter((el) => el._id !== id);
    setRecipes(newRecipes);
  }

  // Map through the recipes and display them
  function recipeList() {
    return recipes.map((recipe) => (
      <Recipe
        recipe={recipe}
        deleteRecipe={deleteRecipe}
        key={recipe._id}
      />
    ));
  }

  return (
    <div>
      <h3>Recipe List</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Recipe Name</th>
            <th>Ingredients</th>
            <th>Instructions</th>
          </tr>
        </thead>
        <tbody>{recipeList()}</tbody>
      </table>
    </div>
  );
}
